import { useParams, useSearchParams, useNavigate } from 'react-router-dom';
import io from 'socket.io-client';
import styled from 'styled-components';
import { 
  Mic, 
  MicOff, 
  Video, 
  VideoOff, 
  Monitor, 
  MessageSquare, 
  Phone, 
  Users,
  Send
} from 'lucide-react';

const Container = styled.div`
  min-height: 100vh;
  background: #1a1a1a;
  display: flex;
