const VideoRoom = () => {
  const { roomId } = useParams();
  const [searchParams] = useSearchParams();
  const navigate = useNavigate();

  const [socket, setSocket] = useState(null);
  const [localStream, setLocalStream] = useState(null);
  const [remoteStreams, setRemoteStreams] = useState(new Map());
  const [isAudioMuted, setIsAudioMuted] = useState(false);
  const [isVideoMuted, setIsVideoMuted] = useState(false);
  const [isScreenSharing, setIsScreenSharing] = useState(false);
  const [showChat, setShowChat] = useState(false);
  const [messages, setMessages] = useState([]);
  const [newMessage, setNewMessage] = useState('');
  const [participants, setParticipants] = useState(1);

  const localVideoRef = useRef(null);
  const remoteVideoRefs = useRef(new Map());
  const peerConnections = useRef(new Map());
  const userName = searchParams.get('name') || 'Anonymous';

  // Initialize socket connection
  useEffect(() => {
    const newSocket = io('http://localhost:5001');
    setSocket(newSocket);

    return () => {
      newSocket.close();
    };
  }, []);

  // Initialize local media stream
  useEffect(() => {
    const initLocalStream = async () => {
      try {
        const stream = await navigator.mediaDevices.getUserMedia({
          video: true,
          audio: true
        });
        setLocalStream(stream);
        if (localVideoRef.current) {
          localVideoRef.current.srcObject = stream;
        }
      } catch (error) {
        console.error('Error accessing media devices:', error);
        alert('Unable to access camera and microphone. Please check permissions.');
      }
    };

    initLocalStream();
  }, []);

  // Socket event handlers
  useEffect(() => {
    if (!socket) return;

    const handleUserConnected = (userId) => {
      console.log('User connected:', userId);
      setParticipants(prev => prev + 1);
      createPeerConnection(userId);
    };

    const handleUserDisconnected = (userId) => {
      console.log('User disconnected:', userId);
      setParticipants(prev => prev - 1);
      if (peerConnections.current.has(userId)) {
        peerConnections.current.get(userId).close();
        peerConnections.current.delete(userId);
      }
      setRemoteStreams(prev => {
        const newStreams = new Map(prev);
        newStreams.delete(userId);
        return newStreams;
      });
    };

    const handleOffer = async (data) => {
      console.log('Received offer from:', data.userId);
      await handleOfferReceived(data);
    };

    const handleAnswer = async (data) => {
      console.log('Received answer from:', data.userId);
      await handleAnswerReceived(data);
    };

    const handleIceCandidate = async (data) => {
      console.log('Received ICE candidate from:', data.userId);
      await handleIceCandidateReceived(data);
    };

    const handleMessage = (data) => {
      setMessages(prev => [...prev, data]);
    };

    socket.on('user-connected', handleUserConnected);
    socket.on('user-disconnected', handleUserDisconnected);
    socket.on('offer', handleOffer);
    socket.on('answer', handleAnswer);
    socket.on('ice-candidate', handleIceCandidate);
    socket.on('receive-message', handleMessage);

    return () => {
      socket.off('user-connected', handleUserConnected);
      socket.off('user-disconnected', handleUserDisconnected);
      socket.off('offer', handleOffer);
      socket.off('answer', handleAnswer);
      socket.off('ice-candidate', handleIceCandidate);
      socket.off('receive-message', handleMessage);
    };
  }, [socket]);

  // Join room when socket is ready
  useEffect(() => {
    if (socket && roomId) {
      socket.emit('join-room', roomId, userName);
    }
  }, [socket, roomId, userName]);

  // Create peer connection
  const createPeerConnection = useCallback((userId) => {
    const peerConnection = new RTCPeerConnection({
      iceServers: [
        { urls: 'stun:stun.l.google.com:19302' },
        { urls: 'stun:stun1.l.google.com:19302' }
      ]
    });

    // Add local stream tracks
    if (localStream) {
      localStream.getTracks().forEach(track => {
        peerConnection.addTrack(track, localStream);
      });
    }

    // Handle remote stream
    peerConnection.ontrack = (event) => {
      console.log('Received remote stream from:', userId);
      const [remoteStream] = event.streams;
      setRemoteStreams(prev => new Map(prev).set(userId, remoteStream));
    };

    // Handle ICE candidates
    peerConnection.onicecandidate = (event) => {
      if (event.candidate) {
        socket.emit('ice-candidate', {
          candidate: event.candidate,
          roomId,
          userId
        });
      }
    };

    peerConnections.current.set(userId, peerConnection);
  }, [localStream, socket, roomId]);

  // Handle offer received
  const handleOfferReceived = useCallback(async (data) => {
    const { offer, userId } = data;
    let peerConnection = peerConnections.current.get(userId);

    if (!peerConnection) {
      createPeerConnection(userId);
      peerConnection = peerConnections.current.get(userId);
    }

    try {
      await peerConnection.setRemoteDescription(new RTCSessionDescription(offer));
      const answer = await peerConnection.createAnswer();
      await peerConnection.setLocalDescription(answer);

      socket.emit('answer', {
        answer,
        roomId,
        userId
      });
    } catch (error) {
      console.error('Error handling offer:', error);
    }
  }, [createPeerConnection, socket, roomId]);

  // Handle answer received
  const handleAnswerReceived = useCallback(async (data) => {
    const { answer, userId } = data;
    const peerConnection = peerConnections.current.get(userId);

    if (peerConnection) {
      try {
        await peerConnection.setRemoteDescription(new RTCSessionDescription(answer));
      } catch (error) {
        console.error('Error handling answer:', error);
      }
    }
  }, []);

  // Handle ICE candidate received
  const handleIceCandidateReceived = useCallback(async (data) => {
    const { candidate, userId } = data;
    const peerConnection = peerConnections.current.get(userId);

    if (peerConnection) {
      try {
        await peerConnection.addIceCandidate(new RTCIceCandidate(candidate));
      } catch (error) {
        console.error('Error handling ICE candidate:', error);
      }
    }
  }, []);

  // Send offer to new user
  const sendOffer = useCallback(async (userId) => {
    const peerConnection = peerConnections.current.get(userId);
    if (!peerConnection) return;

    try {
      const offer = await peerConnection.createOffer();
      await peerConnection.setLocalDescription(offer);

      socket.emit('offer', {
        offer,
        roomId,
        userId
      });
    } catch (error) {
      console.error('Error sending offer:', error);
    }
  }, [socket, roomId]);

  // Toggle audio
  const toggleAudio = () => {
    if (localStream) {
      const audioTrack = localStream.getAudioTracks()[0];
      if (audioTrack) {
        audioTrack.enabled = !audioTrack.enabled;
        setIsAudioMuted(!audioTrack.enabled);
      }
    }
  };

  // Toggle video
  const toggleVideo = () => {
    if (localStream) {
      const videoTrack = localStream.getVideoTracks()[0];
      if (videoTrack) {
        videoTrack.enabled = !videoTrack.enabled;
        setIsVideoMuted(!videoTrack.enabled);
      }
    }
  };

  // Toggle screen sharing
  const toggleScreenShare = async () => {
    try {
      if (!isScreenSharing) {
        const screenStream = await navigator.mediaDevices.getDisplayMedia({
          video: true,
          audio: true
        });

        // Replace video track in all peer connections
        const videoTrack = screenStream.getVideoTracks()[0];
        peerConnections.current.forEach(peerConnection => {
          const sender = peerConnection.getSenders().find(s => 
            s.track && s.track.kind === 'video'
          );
          if (sender) {
            sender.replaceTrack(videoTrack);
          }
        });

        // Update local video
        if (localVideoRef.current) {
          localVideoRef.current.srcObject = screenStream;
        }

        setIsScreenSharing(true);

        // Handle screen share end
        videoTrack.onended = () => {
          toggleScreenShare();
        };
      } else {
        // Stop screen sharing and restore camera
        if (localStream) {
          const videoTrack = localStream.getVideoTracks()[0];
          peerConnections.current.forEach(peerConnection => {
            const sender = peerConnection.getSenders().find(s => 
              s.track && s.track.kind === 'video'
            );
            if (sender) {
              sender.replaceTrack(videoTrack);
            }
          });

          if (localVideoRef.current) {
            localVideoRef.current.srcObject = localStream;
          }
        }
        setIsScreenSharing(false);
      }
    } catch (error) {
      console.error('Error toggling screen share:', error);
    }
  };

  // Send message
  const sendMessage = () => {
    if (newMessage.trim() && socket) {
      const message = {
        text: newMessage.trim(),
        sender: userName,
        timestamp: new Date().toISOString()
      };

      socket.emit('send-message', { message, roomId });
      setNewMessage('');
    }
  };

  // Leave room
  const leaveRoom = () => {
    if (socket) {
      socket.emit('leave-room', roomId, userName);
    }

    // Close all peer connections
    peerConnections.current.forEach(peerConnection => {
      peerConnection.close();
    });

    // Stop local stream
    if (localStream) {
      localStream.getTracks().forEach(track => track.stop());
    }

    navigate('/');
  };

  // Handle key press for message input
  const handleKeyPress = (e) => {
    if (e.key === 'Enter') {
      sendMessage();
    }
  };

  return (
    <Container>
      <Header>
        <RoomInfo>
          <Users size={20} />
          <RoomId>Room: {roomId}</RoomId>
          <span>({participants} participants)</span>
        </RoomInfo>
      </Header>

      <MainContent>
        <VideoGrid>
          {/* Local video */}
          <VideoContainer>
            <StyledVideo
              ref={localVideoRef}
              autoPlay
              muted
              playsInline
            />
            <VideoLabel>{userName} (You)</VideoLabel>
          </VideoContainer>

          {/* Remote videos */}
          {Array.from(remoteStreams.entries()).map(([userId, stream]) => (
            <VideoContainer key={userId}>
              <Video
                ref={ref => {
                  if (ref) {
                    ref.srcObject = stream;
                    remoteVideoRefs.current.set(userId, ref);
                  }
                }}
                autoPlay
                playsInline
              />
              <VideoLabel>{userId}</VideoLabel>
            </VideoContainer>
          ))}
        </VideoGrid>

        {showChat && (
          <ChatPanel>
            <ChatHeader>Chat</ChatHeader>
            <ChatMessages>
              {messages.map((message, index) => (
                <Message key={index} isOwn={message.sender === userName}>
                  <MessageSender>{message.sender}</MessageSender>
                  {message.text}
                </Message>
              ))}
            </ChatMessages>
            <ChatInput>
              <MessageInput
                type="text"
                placeholder="Type a message..."
                value={newMessage}
                onChange={(e) => setNewMessage(e.target.value)}
                onKeyPress={handleKeyPress}
              />
              <SendButton onClick={sendMessage}>
                <Send size={16} />
              </SendButton>
            </ChatInput>
          </ChatPanel>
        )}
      </MainContent>

      <Controls>
        <ControlButton
          onClick={toggleAudio}
          active={!isAudioMuted}
          title={isAudioMuted ? 'Unmute' : 'Mute'}
        >
          {isAudioMuted ? <MicOff size={20} /> : <Mic size={20} />}
        </ControlButton>

        <ControlButton
          onClick={toggleVideo}
          active={!isVideoMuted}
          title={isVideoMuted ? 'Turn on camera' : 'Turn off camera'}
        >
          {isVideoMuted ? <VideoOff size={20} /> : <Video size={20} />}
        </ControlButton>

        <ControlButton
          onClick={toggleScreenShare}
          active={isScreenSharing}
          title={isScreenSharing ? 'Stop sharing' : 'Share screen'}
        >
          <Monitor size={20} />
        </ControlButton>

        <ControlButton
          onClick={() => setShowChat(!showChat)}
          active={showChat}
          title="Toggle chat"
        >
          <MessageSquare size={20} />
        </ControlButton>

        <ControlButton
          onClick={leaveRoom}
          className="leave"
          title="Leave room"
        >
          <Phone size={20} />
        </ControlButton>
      </Controls>
    </Container>
  );
};

export default VideoRoom;
